def course_correction(myheading, targetbearing):
    unadjusted= targetbearing-myheading
    if unadjusted < -180:
        return unadjusted +360
    if unadjusted > 180:
        return unadjusted -360
    return unadjusted

def mean_anomaly_from_true_anomaly(orbit, theta):
    e = orbit.eccentricity
    adj = e + math.cos(theta)
    hyp = 1 + e*math.cos(theta)
    return math.acos(adj / hyp)

