def control(self, dt, error, prevError):
    prop = self.pid.proportional(error)
    integral = self.pid.integral(error, dt)
    deriv = self.pid.derivative(dt, error, prevError)
    return self.pid.sum(prop, integral, deriv)

def getInformationFromAcceleration(self, acc, dt):
    info = [acc,0,0]
    info[1] = acc*dt
    info[2] = 0.5*acc*dt*dt

    return tuple(info)

def integral(self, error, dt):

    #prevent a glitch in the matrix
    if self.Igain == 0:
        return self.Integral

    self.Integral = (self.Integral + error*dt)*self.Igain
    if(self.Integral >= self.maxI):
        self.Integral = self.maxI
        return self.Integral
    elif(self.Integral <= self.lowI):
        self.Integral = self.lowI
        return self.Integral
    else:
        return self.Integral

class PIDCon:

    Pgain = 0.0
    Igain = 0.0
    Dgain = 0.0

    Integral = 0
    maxI = 1.0
    lowI = -1.0

    '''GETTERS'''
    def getPgain(self):
        return self.Pgain

    def getIgain(self):
        return self.Igain

    def getDgain(self):
        return self.Dgain

    def getMaxI(self):
        return self.maxI

    def getLowI(self):
        return self.lowI

    '''SETTERS'''
    def setPgain(self, Pgain):
        self.Pgain = Pgain

    def setIgain(self, Igain):
        self.Igain = Igain

    def setDgain(self, Dgain):
        self.Dgain = Dgain

    def getMaxI(self, maxI):
        self.maxI = maxI

    def getLowI(self, lowI):
        self.lowI = lowI


    '''CONSTRUCTOR'''
    def __init__(self, PGain, IGain, DGain, MaxI, LowI):
        self.Pgain = PGain
        self.Igain = IGain
        self.Dgain = DGain

        self.maxI = MaxI
        self.lowI = LowI

    def resetPID(self):
        self.Integral = 0

    '''PID Functions'''
    def derivative(self, dt, error, prevError):
        return ((error - prevError)/dt)*self.Dgain

    def integral(self, error, dt):

        #prevent a glitch in the matrix
        if self.Igain == 0:
            return self.Integral

        self.Integral = (self.Integral + error*dt)*self.Igain
        if(self.Integral >= self.maxI):
            self.Integral = self.maxI
            return self.Integral
        elif(self.Integral <= self.lowI):
            self.Integral = self.lowI
            return self.Integral
        else:
            return self.Integral

    def proportional(self, error):
        return error*self.Pgain

    def sum(self, prop, integral, derived):
        return prop + integral + derived

